mapper.select:
SELECT b.baton_type AS batonType, b.charge AS chatBatonDuration, mem.profile_image_url AS profileImageUrl, mem.nickname AS nickname, m.company_name AS companyName, mem.job_name AS jobName, mem.job_name AS subJobName, mem.career_years AS careerYears
FROM baton b LEFT JOIN mentor m ON b.mentor_id = m.id LEFT JOIN members mem ON m.id = mem.id
WHERE b.mentee_id = #{menteeId} AND b.status = 'REQUESTED'
AND b.baton_type = #{batonType}
ORDER BY b.id ASC LIMIT #{size} OFFSET #{offset}
